/**
 * Button Component
 *
 * A production-grade button with animations, loading states, and full accessibility.
 * Follows the component philosophy: open (full source), reliable (battle-tested),
 * comprehensive (rich API), and customizable.
 *
 * @packageDocumentation
 */

"use client";

// =============================================================================
// IMPORTS
// =============================================================================

// 1. React imports
import * as React from "react";
import {
  forwardRef,
  useCallback,
  useState,
  useEffect,
  type ComponentPropsWithoutRef,
  type ElementRef,
} from "react";

// 2. External library imports
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";
import { motion, type HTMLMotionProps } from "motion/react";
import type { Icon } from "@phosphor-icons/react";

// 3. Internal imports
import { cn } from "@/lib/utils";
import { springPresets, gestures } from "@/lib/animations";
import { LoadingSpinner, AnimatedCheck } from "@/lib/icons";
import { useShouldDisableAnimation } from "@/components/motion-provider";

// =============================================================================
// TYPES
// =============================================================================

/**
 * Ripple effect data structure
 */
interface Ripple {
  x: number;
  y: number;
  size: number;
  key: number;
}

// =============================================================================
// HOOKS
// =============================================================================

/**
 * Hook to manage ripple effects on button click
 *
 * Creates expanding circular ripples from the click point,
 * automatically cleaning up after animation completes.
 */
function useRipple() {
  const [ripples, setRipples] = useState<Ripple[]>([]);

  const createRipple = useCallback(
    (event: React.MouseEvent<HTMLButtonElement>) => {
      const button = event.currentTarget;
      const rect = button.getBoundingClientRect();
      const size = Math.max(rect.width, rect.height);
      const x = event.clientX - rect.left - size / 2;
      const y = event.clientY - rect.top - size / 2;

      const newRipple: Ripple = { x, y, size, key: Date.now() };
      setRipples((prev) => [...prev, newRipple]);
    },
    [],
  );

  // Auto-cleanup ripples after animation
  useEffect(() => {
    if (ripples.length > 0) {
      const lastRipple = ripples[ripples.length - 1];
      const timeout = setTimeout(() => {
        setRipples((prev) => prev.filter((r) => r.key !== lastRipple.key));
      }, 600);
      return () => clearTimeout(timeout);
    }
  }, [ripples]);

  return { ripples, createRipple };
}

// =============================================================================
// VARIANTS (CVA)
// Defined outside component for performance
// =============================================================================

/**
 * Button variants using class-variance-authority
 *
 * Includes all standard variants plus refinements for shadows,
 * focus states, and accessibility.
 */
const buttonVariants = cva(
  // Base styles - always applied
  [
    // Layout
    "inline-flex items-center justify-center gap-2 whitespace-nowrap shrink-0",
    // Typography
    "text-sm font-medium",
    // Shape
    "rounded-lg",
    // Transitions
    "transition-colors duration-150",
    // Disabled state
    "disabled:pointer-events-none disabled:opacity-50",
    // Icon sizing
    "[&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 [&_svg]:shrink-0",
    // Focus ring
    "outline-none",
    "focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background",
    // Error state (aria-invalid)
    "aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
    // Usability
    "select-none",
  ].join(" "),
  {
    variants: {
      /**
       * Visual style variants
       */
      variant: {
        default: [
          "bg-primary text-primary-foreground",
          "shadow-sm shadow-primary/20",
          "hover:bg-primary/90 hover:shadow-md hover:shadow-primary/25",
          "active:shadow-sm",
        ].join(" "),

        destructive: [
          "bg-destructive text-white",
          "shadow-sm shadow-destructive/20",
          "hover:bg-destructive/90 hover:shadow-md hover:shadow-destructive/25",
          "focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40",
          "dark:bg-destructive/80",
        ].join(" "),

        outline: [
          "border border-input bg-background",
          "shadow-xs",
          "hover:bg-accent hover:text-accent-foreground hover:border-accent-foreground/20",
          "dark:bg-input/30 dark:border-input dark:hover:bg-input/50",
        ].join(" "),

        secondary: [
          "bg-secondary text-secondary-foreground",
          "shadow-xs",
          "hover:bg-secondary/80 hover:shadow-sm",
        ].join(" "),

        ghost: [
          "hover:bg-accent hover:text-accent-foreground",
          "dark:hover:bg-accent/50",
        ].join(" "),

        link: ["text-primary underline-offset-4", "hover:underline"].join(" "),
      },

      /**
       * Size variants
       */
      size: {
        default: "h-10 px-5 py-2.5 has-[>svg]:px-4",
        sm: "h-9 rounded-md gap-1.5 px-4 text-xs has-[>svg]:px-3",
        lg: "h-11 rounded-lg px-7 text-base has-[>svg]:px-5",
        xl: "h-12 rounded-xl px-8 text-base has-[>svg]:px-6",
        icon: "size-10",
        "icon-sm": "size-9",
        "icon-lg": "size-11",
      },
    },

    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

// =============================================================================
// COMPONENT PROPS
// =============================================================================

/**
 * Button component props
 */
export interface ButtonProps
  extends
    Omit<ComponentPropsWithoutRef<"button">, "ref">,
    VariantProps<typeof buttonVariants> {
  /**
   * Render as a different element using Radix Slot pattern.
   * When true, button merges props with its child element.
   *
   * @example
   * ```tsx
   * <Button asChild>
   *   <a href="/home">Go Home</a>
   * </Button>
   * ```
   */
  asChild?: boolean;

  /**
   * Show loading spinner and disable interactions.
   * Replaces iconStart with a spinner.
   */
  loading?: boolean;

  /**
   * Show success state with animated checkmark.
   * Replaces iconStart with a check icon.
   */
  success?: boolean;

  /**
   * Icon to display at the start (left side).
   * Hidden during loading/success states.
   */
  iconStart?: Icon;

  /**
   * Icon to display at the end (right side).
   * Hidden during loading/success states.
   */
  iconEnd?: Icon;

  /**
   * Disable all animations for this button.
   * Overrides global motion provider settings.
   */
  disableAnimation?: boolean;
}

// =============================================================================
// BUTTON COMPONENT
// =============================================================================

/**
 * Button - Unified button component with animations and rich features.
 *
 * Features:
 * - All standard variants (default, destructive, outline, secondary, ghost, link)
 * - Motion press feedback (scale on tap)
 * - Ripple effect on click
 * - Loading state with animated spinner
 * - Success state with checkmark
 * - Icon support with proper positioning
 * - Respects reduced motion preferences
 * - Full ARIA compliance
 * - Polymorphic with asChild pattern
 *
 * @example
 * ```tsx
 * // Basic usage
 * <Button variant="default" size="lg">
 *   Submit
 * </Button>
 *
 * // With loading state
 * <Button loading={isSubmitting}>
 *   Save Changes
 * </Button>
 *
 * // With icons
 * <Button variant="outline" iconStart={PlusIcon}>
 *   Add Item
 * </Button>
 *
 * // As a link
 * <Button asChild>
 *   <a href="/dashboard">Dashboard</a>
 * </Button>
 * ```
 */
const Button = forwardRef<ElementRef<"button">, ButtonProps>(
  (
    {
      className,
      variant,
      size,
      asChild = false,
      loading = false,
      success = false,
      iconStart: IconStart,
      iconEnd: IconEnd,
      disableAnimation,
      disabled,
      children,
      onClick,
      ...props
    },
    ref,
  ) => {
    // Animation preference
    const shouldDisableAnimation = useShouldDisableAnimation(disableAnimation);
    const isDisabled = disabled || loading;

    // Ripple effect management
    const { ripples, createRipple } = useRipple();

    // Determine if ripple should show (not on ghost/link variants)
    const showRipple = variant !== "ghost" && variant !== "link";

    /**
     * Handle click with ripple creation
     */
    const handleClick = useCallback(
      (event: React.MouseEvent<HTMLButtonElement>) => {
        if (!loading && !success && showRipple) {
          createRipple(event);
        }
        onClick?.(event);
      },
      [loading, success, showRipple, createRipple, onClick],
    );

    // =========================================================================
    // Determine icon sizes based on button size
    // =========================================================================
    const iconSize = size === "sm" || size === "icon-sm" ? 14 : 16;

    // =========================================================================
    // Icon content - stable rendering without AnimatePresence
    // =========================================================================
    const iconStartContent =
      IconStart && !loading && !success ? (
        <IconStart
          key="icon-start"
          className="shrink-0"
          size={iconSize}
          aria-hidden="true"
        />
      ) : null;

    const loadingSpinner = loading ? (
      <LoadingSpinner
        key="loading-spinner"
        size={iconSize}
        aria-hidden="true"
      />
    ) : null;

    const successCheck =
      success && !loading ? (
        <AnimatedCheck
          key="success-check"
          size={iconSize}
          className="shrink-0"
          aria-hidden="true"
        />
      ) : null;

    const iconEndContent =
      IconEnd && !loading && !success ? (
        <IconEnd
          key="icon-end"
          className="shrink-0"
          size={iconSize}
          aria-hidden="true"
        />
      ) : null;

    // =========================================================================
    // Content wrapper with icons and children
    // =========================================================================
    const buttonContent = (
      <>
        <span className="relative z-10 flex items-center justify-center gap-2">
          {loadingSpinner}
          {successCheck}
          {iconStartContent}
          {children}
          {iconEndContent}
        </span>

        {/* Ripple container */}
        {showRipple && (
          <span
            className="pointer-events-none absolute inset-0 overflow-hidden"
            style={{
              opacity: loading || success ? 0 : 1,
              transition: "opacity 200ms ease-out",
            }}
            aria-hidden="true"
          >
            {ripples.map((ripple) => (
              <span
                className="animate-rippling absolute rounded-full opacity-30"
                key={ripple.key}
                style={{
                  width: `${ripple.size}px`,
                  height: `${ripple.size}px`,
                  top: `${ripple.y}px`,
                  left: `${ripple.x}px`,
                  backgroundColor: "currentColor",
                  transform: "scale(0)",
                }}
              />
            ))}
          </span>
        )}
      </>
    );

    // =========================================================================
    // Common props for all button variations
    // =========================================================================
    const commonProps = {
      "data-slot": "button",
      "data-loading": loading || undefined,
      "data-success": success || undefined,
      className: cn(
        buttonVariants({ variant, size, className }),
        showRipple && "relative overflow-hidden",
      ),
      disabled: isDisabled,
      "aria-busy": loading || undefined,
      "aria-disabled": isDisabled || undefined,
      onClick: handleClick,
      ...props,
    };

    // =========================================================================
    // Render: asChild pattern using Slot
    // =========================================================================
    if (asChild) {
      return (
        <Slot ref={ref} {...commonProps}>
          {children}
        </Slot>
      );
    }

    // =========================================================================
    // Render: Non-animated version
    // =========================================================================
    if (shouldDisableAnimation) {
      return (
        <button ref={ref} {...commonProps}>
          {buttonContent}
        </button>
      );
    }

    // =========================================================================
    // Render: Animated version with motion.button
    // =========================================================================
    // Extract motion-conflicting props
    const {
      onAnimationStart: _onAnimationStart,
      onDrag: _onDrag,
      onDragStart: _onDragStart,
      onDragEnd: _onDragEnd,
      ...restCommonProps
    } = commonProps as typeof commonProps & Partial<HTMLMotionProps<"button">>;

    return (
      <motion.button
        ref={ref}
        {...restCommonProps}
        whileTap={isDisabled ? undefined : gestures.button.whileTap}
        whileHover={isDisabled ? undefined : gestures.button.whileHover}
        transition={springPresets.interactive}
      >
        {buttonContent}
      </motion.button>
    );
  },
);

Button.displayName = "Button";

// =============================================================================
// ICON BUTTON COMPONENT
// Specialized button for icon-only usage
// =============================================================================

/**
 * IconButton component props
 */
export interface IconButtonProps extends Omit<
  ButtonProps,
  "iconStart" | "iconEnd" | "children" | "asChild"
> {
  /**
   * The icon to display (required)
   */
  icon: Icon;

  /**
   * Accessible label (required for icon-only buttons)
   */
  "aria-label": string;
}

/**
 * IconButton - Icon-only button with proper accessibility
 *
 * Forces an aria-label for accessibility since there's no visible text.
 *
 * @example
 * ```tsx
 * <IconButton icon={XIcon} aria-label="Close dialog" variant="ghost" />
 * ```
 */
const IconButton = forwardRef<ElementRef<"button">, IconButtonProps>(
  ({ icon, size = "icon", ...props }, ref) => {
    return <Button ref={ref} size={size} iconStart={icon} {...props} />;
  },
);

IconButton.displayName = "IconButton";

// =============================================================================
// EXPORTS
// =============================================================================

export { Button, IconButton, buttonVariants };
export type { ButtonProps as ButtonRootProps };

"use client";

import * as React from "react";
import { createContext, useContext, useMemo } from "react";
import { MotionConfig, type Transition } from "motion/react";
import { useReducedMotion } from "@/hooks/use-reduced-motion";
import { useLowPowerDevice } from "@/hooks/use-low-power-device";
import { transitions } from "@/lib/motion";

// =============================================================================
// TYPES
// =============================================================================

export type ReducedMotionSetting = "user" | "always" | "never";

export type MotionContextValue = {
  /** Current reduced motion setting */
  reducedMotion: ReducedMotionSetting;
  /** Whether animations are effectively disabled */
  isAnimationDisabled: boolean;
  /** Whether low-power mode auto-disable is enabled */
  lowPowerAutoDisable: boolean;
  /** Low power device state */
  lowPowerState: {
    isLowPower: boolean;
    batteryLevel: number | null;
    isCharging: boolean | null;
  };
  /** Default transition based on current settings */
  defaultTransition: Transition;
};

export type MotionProviderProps = {
  children: React.ReactNode;
  /** How to handle reduced motion: 'user' (system preference), 'always' (force reduced), 'never' (force animations) */
  reducedMotion?: ReducedMotionSetting;
  /** Automatically disable animations on low-power devices */
  lowPowerAutoDisable?: boolean;
  /** Default transition for all Motion components */
  defaultTransition?: Transition;
  /** Additional className for the provider wrapper */
  className?: string;
};

// =============================================================================
// CONTEXT
// =============================================================================

const MotionContext = createContext<MotionContextValue | null>(null);

/**
 * Hook to access motion configuration
 *
 * @returns Motion context value with animation settings
 * @throws Error if used outside of MotionProvider
 *
 * @example
 * ```tsx
 * function AnimatedButton() {
 *   const { isAnimationDisabled } = useMotionConfig();
 *
 *   return (
 *     <motion.button
 *       whileTap={isAnimationDisabled ? undefined : { scale: 0.95 }}
 *     />
 *   );
 * }
 * ```
 */
export function useMotionConfig(): MotionContextValue {
  const context = useContext(MotionContext);

  if (!context) {
    throw new Error("useMotionConfig must be used within a MotionProvider");
  }

  return context;
}

/**
 * Hook to check if animations should be disabled for a component
 *
 * @param disableAnimation - Optional local override to disable animation
 * @returns Whether animations should be disabled
 *
 * @example
 * ```tsx
 * function Button({ disableAnimation }) {
 *   const shouldDisable = useShouldDisableAnimation(disableAnimation);
 *
 *   if (shouldDisable) {
 *     return <button>Click me</button>;
 *   }
 *
 *   return <motion.button whileTap={{ scale: 0.95 }}>Click me</motion.button>;
 * }
 * ```
 */
export function useShouldDisableAnimation(disableAnimation?: boolean): boolean {
  const context = useContext(MotionContext);
  const prefersReducedMotion = useReducedMotion();

  // Local override takes precedence
  if (disableAnimation !== undefined) {
    return disableAnimation;
  }

  // If no provider, check system preference directly
  if (!context) {
    return prefersReducedMotion;
  }

  return context.isAnimationDisabled;
}

/**
 * Hook to get the appropriate transition based on animation settings
 *
 * @param transition - Desired transition when animations are enabled
 * @param disableAnimation - Optional local override
 * @returns The transition to use (reduced if animations disabled)
 */
export function useAnimationTransition(
  transition: Transition = transitions.default,
  disableAnimation?: boolean,
): Transition {
  const shouldDisable = useShouldDisableAnimation(disableAnimation);

  if (shouldDisable) {
    return transitions.reduced;
  }

  return transition;
}

// =============================================================================
// PROVIDER COMPONENT
// =============================================================================

/**
 * MotionProvider - Global animation configuration provider
 *
 * Wraps Motion's MotionConfig with additional context for:
 * - Reduced motion preferences (system or manual)
 * - Low-power device detection
 * - Global transition defaults
 *
 * @example
 * ```tsx
 * // In your app layout
 * <MotionProvider
 *   reducedMotion="user"
 *   lowPowerAutoDisable
 * >
 *   <App />
 * </MotionProvider>
 * ```
 */
export function MotionProvider({
  children,
  reducedMotion = "user",
  lowPowerAutoDisable = true,
  defaultTransition = transitions.default,
}: MotionProviderProps) {
  // System preference for reduced motion
  const prefersReducedMotion = useReducedMotion();

  // Low-power device detection
  const lowPowerState = useLowPowerDevice();

  // Determine if animations should be disabled
  const isAnimationDisabled = useMemo(() => {
    // 'always' forces reduced motion
    if (reducedMotion === "always") {
      return true;
    }

    // 'never' forces animations (ignores preferences)
    if (reducedMotion === "never") {
      return false;
    }

    // 'user' respects system preference
    if (prefersReducedMotion) {
      return true;
    }

    // Check low-power mode if auto-disable is enabled
    if (lowPowerAutoDisable && lowPowerState.shouldReduceAnimations) {
      return true;
    }

    return false;
  }, [
    reducedMotion,
    prefersReducedMotion,
    lowPowerAutoDisable,
    lowPowerState.shouldReduceAnimations,
  ]);

  // Get effective transition
  const effectiveTransition = useMemo(() => {
    if (isAnimationDisabled) {
      return transitions.reduced;
    }
    return defaultTransition;
  }, [isAnimationDisabled, defaultTransition]);

  // Context value
  const contextValue = useMemo<MotionContextValue>(
    () => ({
      reducedMotion,
      isAnimationDisabled,
      lowPowerAutoDisable,
      lowPowerState: {
        isLowPower: lowPowerState.isLowPower,
        batteryLevel: lowPowerState.batteryLevel,
        isCharging: lowPowerState.isCharging,
      },
      defaultTransition: effectiveTransition,
    }),
    [
      reducedMotion,
      isAnimationDisabled,
      lowPowerAutoDisable,
      lowPowerState.isLowPower,
      lowPowerState.batteryLevel,
      lowPowerState.isCharging,
      effectiveTransition,
    ],
  );

  return (
    <MotionContext.Provider value={contextValue}>
      <MotionConfig
        transition={effectiveTransition}
        reducedMotion={isAnimationDisabled ? "always" : "user"}
      >
        {children}
      </MotionConfig>
    </MotionContext.Provider>
  );
}

// =============================================================================
// EXPORTS
// =============================================================================

export { MotionContext };

"use client";

import { useSyncExternalStore, useCallback } from "react";

/**
 * Hook to detect user's reduced motion preference
 *
 * Uses the `prefers-reduced-motion` media query to detect if the user
 * has requested reduced motion in their system settings.
 *
 * @param override - Optional boolean to override the system preference
 * @returns Whether reduced motion is preferred
 *
 * @example
 * ```tsx
 * function MyComponent() {
 *   const prefersReducedMotion = useReducedMotion();
 *
 *   return (
 *     <motion.div
 *       animate={{ x: prefersReducedMotion ? 0 : 100 }}
 *     />
 *   );
 * }
 * ```
 */
export function useReducedMotion(override?: boolean): boolean {
  const subscribe = useCallback((callback: () => void) => {
    if (typeof window === "undefined") return () => {};

    const mediaQuery = window.matchMedia("(prefers-reduced-motion: reduce)");

    mediaQuery.addEventListener("change", callback);
    return () => mediaQuery.removeEventListener("change", callback);
  }, []);

  const getSnapshot = useCallback(() => {
    if (typeof window === "undefined") return false;
    return window.matchMedia("(prefers-reduced-motion: reduce)").matches;
  }, []);

  // Server snapshot should always return false for SSR
  const getServerSnapshot = useCallback(() => false, []);

  const prefersReducedMotion = useSyncExternalStore(
    subscribe,
    getSnapshot,
    getServerSnapshot,
  );

  // Override takes precedence if provided
  if (override !== undefined) {
    return override;
  }

  return prefersReducedMotion;
}

export default useReducedMotion;

"use client";

import { useSyncExternalStore, useCallback } from "react";

/**
 * Hook to detect if the device is in a low-power state
 *
 * Checks Battery API and hardware concurrency to determine if
 * animations should be reduced for performance reasons.
 *
 * @returns Object with power state information
 *
 * @example
 * ```tsx
 * function AnimatedComponent() {
 *   const { isLowPower, shouldReduceAnimations } = useLowPowerDevice();
 *
 *   return (
 *     <motion.div
 *       animate={{ scale: shouldReduceAnimations ? 1 : 1.1 }}
 *     />
 *   );
 * }
 * ```
 */

type BatteryManager = {
  charging: boolean;
  level: number;
  addEventListener: (type: string, listener: () => void) => void;
  removeEventListener: (type: string, listener: () => void) => void;
};

type NavigatorWithBattery = Navigator & {
  getBattery?: () => Promise<BatteryManager>;
};

export type LowPowerState = {
  /** Whether the device is considered low-power */
  isLowPower: boolean;
  /** Whether animations should be reduced */
  shouldReduceAnimations: boolean;
  /** Battery level (0-1) if available */
  batteryLevel: number | null;
  /** Whether the device is charging */
  isCharging: boolean | null;
  /** Number of logical CPU cores */
  hardwareConcurrency: number;
};

// Battery state cache
let batteryManager: BatteryManager | null = null;
let batteryPromise: Promise<BatteryManager | null> | null = null;

// Get battery manager (cached)
async function getBatteryManager(): Promise<BatteryManager | null> {
  if (batteryManager) return batteryManager;
  if (batteryPromise) return batteryPromise;

  if (typeof navigator === "undefined") return null;

  const nav = navigator as NavigatorWithBattery;
  if (!nav.getBattery) return null;

  batteryPromise = nav.getBattery().catch(() => null);
  batteryManager = await batteryPromise;
  return batteryManager;
}

// Initialize battery manager on module load
if (typeof window !== "undefined") {
  getBatteryManager();
}

// Server snapshot - defined once at module level to avoid re-creation
const serverSnapshot: LowPowerState = {
  isLowPower: false,
  shouldReduceAnimations: false,
  batteryLevel: null,
  isCharging: null,
  hardwareConcurrency: 4,
};

// Cache the current snapshot to avoid unnecessary re-renders
let cachedSnapshot: LowPowerState = serverSnapshot;

// Function to get current snapshot (called outside of React)
function getCurrentSnapshot(): LowPowerState {
  if (typeof window === "undefined") {
    return serverSnapshot;
  }

  const hardwareConcurrency = navigator.hardwareConcurrency || 4;
  const isLowEndDevice = hardwareConcurrency <= 4;

  // Check battery if available
  let batteryLevel: number | null = null;
  let isCharging: boolean | null = null;
  let isLowBattery = false;

  if (batteryManager) {
    batteryLevel = batteryManager.level;
    isCharging = batteryManager.charging;
    // Consider low battery if below 20% and not charging
    isLowBattery = batteryLevel < 0.2 && !isCharging;
  }

  const isLowPower = isLowEndDevice || isLowBattery;
  const shouldReduceAnimations = isLowPower;

  // Only create new object if values actually changed
  const newSnapshot = {
    isLowPower,
    shouldReduceAnimations,
    batteryLevel,
    isCharging,
    hardwareConcurrency,
  };

  // Compare with cached snapshot
  if (
    cachedSnapshot.isLowPower === newSnapshot.isLowPower &&
    cachedSnapshot.shouldReduceAnimations ===
      newSnapshot.shouldReduceAnimations &&
    cachedSnapshot.batteryLevel === newSnapshot.batteryLevel &&
    cachedSnapshot.isCharging === newSnapshot.isCharging &&
    cachedSnapshot.hardwareConcurrency === newSnapshot.hardwareConcurrency
  ) {
    return cachedSnapshot;
  }

  cachedSnapshot = newSnapshot;
  return cachedSnapshot;
}

export function useLowPowerDevice(): LowPowerState {
  const getSnapshot = () => getCurrentSnapshot();

  const getServerSnapshot = () => serverSnapshot;

  const subscribe = useCallback((callback: () => void) => {
    if (typeof window === "undefined") return () => {};

    // Subscribe to battery events if available
    getBatteryManager().then((battery) => {
      if (battery) {
        battery.addEventListener("chargingchange", callback);
        battery.addEventListener("levelchange", callback);
      }
    });

    // Clean up
    return () => {
      if (batteryManager) {
        batteryManager.removeEventListener("chargingchange", callback);
        batteryManager.removeEventListener("levelchange", callback);
      }
    };
  }, []);

  return useSyncExternalStore(subscribe, getSnapshot, getServerSnapshot);
}

export default useLowPowerDevice;

import type { Transition, Variants } from "motion/react";

// =============================================================================
// SPRING PRESETS
// Spring physics configurations for animations
// =============================================================================

/**
 * Spring presets for consistent animations
 */
export const springPresets = {
  /**
   * Snappy - Quick, responsive interactions (buttons, toggles)
   * High stiffness, high damping = fast and controlled
   */
  snappy: {
    type: "spring" as const,
    stiffness: 400,
    damping: 30,
    mass: 1,
  },

  /**
   * Bouncy - Playful, attention-grabbing (notifications, toasts)
   * Medium stiffness, lower damping = visible bounce
   */
  bouncy: {
    type: "spring" as const,
    stiffness: 300,
    damping: 20,
    mass: 1,
  },

  /**
   * Smooth - Slow transitions (modals, sheets)
   * Lower stiffness, higher damping = graceful movement
   */
  smooth: {
    type: "spring" as const,
    stiffness: 200,
    damping: 25,
    mass: 1,
  },

  /**
   * Gentle - Subtle, background animations (fade, scale)
   * Very low stiffness, high damping = barely perceptible
   */
  gentle: {
    type: "spring" as const,
    stiffness: 150,
    damping: 30,
    mass: 1,
  },

  /**
   * Interactive - Drag and gesture responses
   * Balanced for real-time responsiveness
   */
  interactive: {
    type: "spring" as const,
    stiffness: 500,
    damping: 35,
    mass: 0.8,
  },
} as const;

// =============================================================================
// TRANSITION PRESETS
// Pre-configured transitions for common use cases
// =============================================================================

export const transitions = {
  /** Standard transition using snappy spring */
  default: springPresets.snappy as Transition,

  /** For modal/dialog opening animations */
  modal: springPresets.smooth as Transition,

  /** For dropdown/popover appearance */
  popover: {
    ...springPresets.snappy,
    duration: 0.2,
  } as Transition,

  /** For morphing popover with layoutId */
  morphingPopover: {
    type: "spring" as const,
    bounce: 0.05,
    duration: 0.3,
  } as Transition,

  /** For sheet slide-in animations */
  sheet: {
    ...springPresets.smooth,
    damping: 28,
  } as Transition,

  /** For button press feedback */
  button: {
    ...springPresets.interactive,
    duration: 0.1,
  } as Transition,

  /** For icon state changes */
  icon: {
    ...springPresets.bouncy,
    duration: 0.3,
  } as Transition,

  /** For staggered list items */
  stagger: {
    staggerChildren: 0.03,
    delayChildren: 0.05,
  } as Transition,

  /** Fast fade for overlays */
  fade: {
    type: "tween" as const,
    duration: 0.15,
    ease: "easeOut",
  } as Transition,

  /** Reduced motion fallback - instant transitions */
  reduced: {
    type: "tween" as const,
    duration: 0,
  } as Transition,
} as const;

// =============================================================================
// VARIANT FACTORIES
// Create animation variants for common patterns
// =============================================================================

/**
 * Creates scale variants for press/tap interactions
 */
export function createPressVariants(scale: number = 0.97): Variants {
  return {
    idle: { scale: 1 },
    pressed: { scale },
  };
}

/**
 * Creates hover scale variants
 */
export function createHoverVariants(scale: number = 1.02): Variants {
  return {
    idle: { scale: 1 },
    hovered: { scale },
  };
}

/**
 * Creates fade + scale variants for modal-like components
 */
export function createModalVariants(options?: {
  initialScale?: number;
  exitScale?: number;
}): Variants {
  const { initialScale = 0.95, exitScale = 0.95 } = options ?? {};

  return {
    initial: {
      opacity: 0,
      scale: initialScale,
    },
    animate: {
      opacity: 1,
      scale: 1,
    },
    exit: {
      opacity: 0,
      scale: exitScale,
    },
  };
}

/**
 * Creates slide variants for sheets/drawers
 */
export function createSlideVariants(
  side: "top" | "right" | "bottom" | "left",
): Variants {
  const transforms: Record<
    string,
    { x?: string | number; y?: string | number }
  > = {
    top: { y: "-100%" },
    right: { x: "100%" },
    bottom: { y: "100%" },
    left: { x: "-100%" },
  };

  return {
    initial: { ...transforms[side], opacity: 0 },
    animate: { x: 0, y: 0, opacity: 1 },
    exit: { ...transforms[side], opacity: 0 },
  };
}

/**
 * Creates staggered children variants for lists
 */
export function createStaggerVariants(options?: {
  staggerChildren?: number;
  delayChildren?: number;
}): Variants {
  const { staggerChildren = 0.03, delayChildren = 0.05 } = options ?? {};

  return {
    initial: { opacity: 0 },
    animate: {
      opacity: 1,
      transition: {
        staggerChildren,
        delayChildren,
      },
    },
    exit: {
      opacity: 0,
      transition: {
        staggerChildren: staggerChildren / 2,
        staggerDirection: -1,
      },
    },
  };
}

/**
 * Creates item variants for staggered lists
 */
export function createStaggerItemVariants(options?: {
  y?: number;
  scale?: number;
}): Variants {
  const { y = 10, scale = 0.95 } = options ?? {};

  return {
    initial: { opacity: 0, y, scale },
    animate: { opacity: 1, y: 0, scale: 1 },
    exit: { opacity: 0, y: y / 2, scale },
  };
}

/**
 * Creates morphing popover variants with blur
 */
export function createMorphingPopoverVariants(options?: {
  initialScale?: number;
  exitScale?: number;
  initialBlur?: number;
  exitBlur?: number;
}): Variants {
  const {
    initialScale = 0.9,
    exitScale = 0.95,
    initialBlur = 10,
    exitBlur = 10,
  } = options ?? {};

  return {
    initial: {
      opacity: 0,
      scale: initialScale,
      filter: `blur(${initialBlur}px)`,
    },
    animate: {
      opacity: 1,
      scale: 1,
      filter: "blur(0px)",
      transition: {
        type: "spring",
        bounce: 0,
        duration: 0.3,
        staggerChildren: 0.05,
      },
    },
    exit: {
      opacity: 0,
      scale: exitScale,
      filter: `blur(${exitBlur}px)`,
      transition: {
        duration: 0.2,
      },
    },
  };
}

/**
 * Creates backdrop/overlay variants
 */
export function createBackdropVariants(): Variants {
  return {
    initial: { opacity: 0 },
    animate: { opacity: 1 },
    exit: { opacity: 0 },
  };
}

/**
 * Creates origin-aware scale variants for popovers/dropdowns
 * Scales from the origin point (where it appears from)
 */
export function createPopoverVariants(options?: {
  initialScale?: number;
  originX?: number;
  originY?: number;
}): Variants {
  const { initialScale = 0.95, originX = 0.5, originY = 0 } = options ?? {};

  return {
    initial: {
      opacity: 0,
      scale: initialScale,
      originX,
      originY,
    },
    animate: {
      opacity: 1,
      scale: 1,
    },
    exit: {
      opacity: 0,
      scale: initialScale,
    },
  };
}

