"use client";

import * as React from "react";
import { createContext, useContext, useMemo } from "react";
import { MotionConfig, type Transition } from "motion/react";
import { useReducedMotion } from "@/hooks/use-reduced-motion";
import { useLowPowerDevice } from "@/hooks/use-low-power-device";
import { transitions } from "@/lib/motion";

// =============================================================================
// TYPES
// =============================================================================

export type ReducedMotionSetting = "user" | "always" | "never";

export type MotionContextValue = {
  /** Current reduced motion setting */
  reducedMotion: ReducedMotionSetting;
  /** Whether animations are effectively disabled */
  isAnimationDisabled: boolean;
  /** Whether low-power mode auto-disable is enabled */
  lowPowerAutoDisable: boolean;
  /** Low power device state */
  lowPowerState: {
    isLowPower: boolean;
    batteryLevel: number | null;
    isCharging: boolean | null;
  };
  /** Default transition based on current settings */
  defaultTransition: Transition;
};

export type MotionProviderProps = {
  children: React.ReactNode;
  /** How to handle reduced motion: 'user' (system preference), 'always' (force reduced), 'never' (force animations) */
  reducedMotion?: ReducedMotionSetting;
  /** Automatically disable animations on low-power devices */
  lowPowerAutoDisable?: boolean;
  /** Default transition for all Motion components */
  defaultTransition?: Transition;
  /** Additional className for the provider wrapper */
  className?: string;
};

// =============================================================================
// CONTEXT
// =============================================================================

const MotionContext = createContext<MotionContextValue | null>(null);

/**
 * Hook to access motion configuration
 *
 * @returns Motion context value with animation settings
 * @throws Error if used outside of MotionProvider
 *
 * @example
 * ```tsx
 * function AnimatedButton() {
 *   const { isAnimationDisabled } = useMotionConfig();
 *
 *   return (
 *     <motion.button
 *       whileTap={isAnimationDisabled ? undefined : { scale: 0.95 }}
 *     />
 *   );
 * }
 * ```
 */
export function useMotionConfig(): MotionContextValue {
  const context = useContext(MotionContext);

  if (!context) {
    throw new Error("useMotionConfig must be used within a MotionProvider");
  }

  return context;
}

/**
 * Hook to check if animations should be disabled for a component
 *
 * @param disableAnimation - Optional local override to disable animation
 * @returns Whether animations should be disabled
 *
 * @example
 * ```tsx
 * function Button({ disableAnimation }) {
 *   const shouldDisable = useShouldDisableAnimation(disableAnimation);
 *
 *   if (shouldDisable) {
 *     return <button>Click me</button>;
 *   }
 *
 *   return <motion.button whileTap={{ scale: 0.95 }}>Click me</motion.button>;
 * }
 * ```
 */
export function useShouldDisableAnimation(disableAnimation?: boolean): boolean {
  const context = useContext(MotionContext);
  const prefersReducedMotion = useReducedMotion();

  // Local override takes precedence
  if (disableAnimation !== undefined) {
    return disableAnimation;
  }

  // If no provider, check system preference directly
  if (!context) {
    return prefersReducedMotion;
  }

  return context.isAnimationDisabled;
}

/**
 * Hook to get the appropriate transition based on animation settings
 *
 * @param transition - Desired transition when animations are enabled
 * @param disableAnimation - Optional local override
 * @returns The transition to use (reduced if animations disabled)
 */
export function useAnimationTransition(
  transition: Transition = transitions.default,
  disableAnimation?: boolean,
): Transition {
  const shouldDisable = useShouldDisableAnimation(disableAnimation);

  if (shouldDisable) {
    return transitions.reduced;
  }

  return transition;
}

// =============================================================================
// PROVIDER COMPONENT
// =============================================================================

/**
 * MotionProvider - Global animation configuration provider
 *
 * Wraps Motion's MotionConfig with additional context for:
 * - Reduced motion preferences (system or manual)
 * - Low-power device detection
 * - Global transition defaults
 *
 * @example
 * ```tsx
 * // In your app layout
 * <MotionProvider
 *   reducedMotion="user"
 *   lowPowerAutoDisable
 * >
 *   <App />
 * </MotionProvider>
 * ```
 */
export function MotionProvider({
  children,
  reducedMotion = "user",
  lowPowerAutoDisable = true,
  defaultTransition = transitions.default,
}: MotionProviderProps) {
  // System preference for reduced motion
  const prefersReducedMotion = useReducedMotion();

  // Low-power device detection
  const lowPowerState = useLowPowerDevice();

  // Determine if animations should be disabled
  const isAnimationDisabled = useMemo(() => {
    // 'always' forces reduced motion
    if (reducedMotion === "always") {
      return true;
    }

    // 'never' forces animations (ignores preferences)
    if (reducedMotion === "never") {
      return false;
    }

    // 'user' respects system preference
    if (prefersReducedMotion) {
      return true;
    }

    // Check low-power mode if auto-disable is enabled
    if (lowPowerAutoDisable && lowPowerState.shouldReduceAnimations) {
      return true;
    }

    return false;
  }, [
    reducedMotion,
    prefersReducedMotion,
    lowPowerAutoDisable,
    lowPowerState.shouldReduceAnimations,
  ]);

  // Get effective transition
  const effectiveTransition = useMemo(() => {
    if (isAnimationDisabled) {
      return transitions.reduced;
    }
    return defaultTransition;
  }, [isAnimationDisabled, defaultTransition]);

  // Context value
  const contextValue = useMemo<MotionContextValue>(
    () => ({
      reducedMotion,
      isAnimationDisabled,
      lowPowerAutoDisable,
      lowPowerState: {
        isLowPower: lowPowerState.isLowPower,
        batteryLevel: lowPowerState.batteryLevel,
        isCharging: lowPowerState.isCharging,
      },
      defaultTransition: effectiveTransition,
    }),
    [
      reducedMotion,
      isAnimationDisabled,
      lowPowerAutoDisable,
      lowPowerState.isLowPower,
      lowPowerState.batteryLevel,
      lowPowerState.isCharging,
      effectiveTransition,
    ],
  );

  return (
    <MotionContext.Provider value={contextValue}>
      <MotionConfig
        transition={effectiveTransition}
        reducedMotion={isAnimationDisabled ? "always" : "user"}
      >
        {children}
      </MotionConfig>
    </MotionContext.Provider>
  );
}

// =============================================================================
// EXPORTS
// =============================================================================

export { MotionContext };

"use client";

import { useSyncExternalStore, useCallback } from "react";

/**
 * Hook to detect user's reduced motion preference
 *
 * Uses the `prefers-reduced-motion` media query to detect if the user
 * has requested reduced motion in their system settings.
 *
 * @param override - Optional boolean to override the system preference
 * @returns Whether reduced motion is preferred
 *
 * @example
 * ```tsx
 * function MyComponent() {
 *   const prefersReducedMotion = useReducedMotion();
 *
 *   return (
 *     <motion.div
 *       animate={{ x: prefersReducedMotion ? 0 : 100 }}
 *     />
 *   );
 * }
 * ```
 */
export function useReducedMotion(override?: boolean): boolean {
  const subscribe = useCallback((callback: () => void) => {
    if (typeof window === "undefined") return () => {};

    const mediaQuery = window.matchMedia("(prefers-reduced-motion: reduce)");

    mediaQuery.addEventListener("change", callback);
    return () => mediaQuery.removeEventListener("change", callback);
  }, []);

  const getSnapshot = useCallback(() => {
    if (typeof window === "undefined") return false;
    return window.matchMedia("(prefers-reduced-motion: reduce)").matches;
  }, []);

  // Server snapshot should always return false for SSR
  const getServerSnapshot = useCallback(() => false, []);

  const prefersReducedMotion = useSyncExternalStore(
    subscribe,
    getSnapshot,
    getServerSnapshot,
  );

  // Override takes precedence if provided
  if (override !== undefined) {
    return override;
  }

  return prefersReducedMotion;
}

export default useReducedMotion;

"use client";

import { useSyncExternalStore, useCallback } from "react";

/**
 * Hook to detect if the device is in a low-power state
 *
 * Checks Battery API and hardware concurrency to determine if
 * animations should be reduced for performance reasons.
 *
 * @returns Object with power state information
 *
 * @example
 * ```tsx
 * function AnimatedComponent() {
 *   const { isLowPower, shouldReduceAnimations } = useLowPowerDevice();
 *
 *   return (
 *     <motion.div
 *       animate={{ scale: shouldReduceAnimations ? 1 : 1.1 }}
 *     />
 *   );
 * }
 * ```
 */

type BatteryManager = {
  charging: boolean;
  level: number;
  addEventListener: (type: string, listener: () => void) => void;
  removeEventListener: (type: string, listener: () => void) => void;
};

type NavigatorWithBattery = Navigator & {
  getBattery?: () => Promise<BatteryManager>;
};

export type LowPowerState = {
  /** Whether the device is considered low-power */
  isLowPower: boolean;
  /** Whether animations should be reduced */
  shouldReduceAnimations: boolean;
  /** Battery level (0-1) if available */
  batteryLevel: number | null;
  /** Whether the device is charging */
  isCharging: boolean | null;
  /** Number of logical CPU cores */
  hardwareConcurrency: number;
};

// Battery state cache
let batteryManager: BatteryManager | null = null;
let batteryPromise: Promise<BatteryManager | null> | null = null;

// Get battery manager (cached)
async function getBatteryManager(): Promise<BatteryManager | null> {
  if (batteryManager) return batteryManager;
  if (batteryPromise) return batteryPromise;

  if (typeof navigator === "undefined") return null;

  const nav = navigator as NavigatorWithBattery;
  if (!nav.getBattery) return null;

  batteryPromise = nav.getBattery().catch(() => null);
  batteryManager = await batteryPromise;
  return batteryManager;
}

// Initialize battery manager on module load
if (typeof window !== "undefined") {
  getBatteryManager();
}

// Server snapshot - defined once at module level to avoid re-creation
const serverSnapshot: LowPowerState = {
  isLowPower: false,
  shouldReduceAnimations: false,
  batteryLevel: null,
  isCharging: null,
  hardwareConcurrency: 4,
};

// Cache the current snapshot to avoid unnecessary re-renders
let cachedSnapshot: LowPowerState = serverSnapshot;

// Function to get current snapshot (called outside of React)
function getCurrentSnapshot(): LowPowerState {
  if (typeof window === "undefined") {
    return serverSnapshot;
  }

  const hardwareConcurrency = navigator.hardwareConcurrency || 4;
  const isLowEndDevice = hardwareConcurrency <= 4;

  // Check battery if available
  let batteryLevel: number | null = null;
  let isCharging: boolean | null = null;
  let isLowBattery = false;

  if (batteryManager) {
    batteryLevel = batteryManager.level;
    isCharging = batteryManager.charging;
    // Consider low battery if below 20% and not charging
    isLowBattery = batteryLevel < 0.2 && !isCharging;
  }

  const isLowPower = isLowEndDevice || isLowBattery;
  const shouldReduceAnimations = isLowPower;

  // Only create new object if values actually changed
  const newSnapshot = {
    isLowPower,
    shouldReduceAnimations,
    batteryLevel,
    isCharging,
    hardwareConcurrency,
  };

  // Compare with cached snapshot
  if (
    cachedSnapshot.isLowPower === newSnapshot.isLowPower &&
    cachedSnapshot.shouldReduceAnimations ===
      newSnapshot.shouldReduceAnimations &&
    cachedSnapshot.batteryLevel === newSnapshot.batteryLevel &&
    cachedSnapshot.isCharging === newSnapshot.isCharging &&
    cachedSnapshot.hardwareConcurrency === newSnapshot.hardwareConcurrency
  ) {
    return cachedSnapshot;
  }

  cachedSnapshot = newSnapshot;
  return cachedSnapshot;
}

export function useLowPowerDevice(): LowPowerState {
  const getSnapshot = () => getCurrentSnapshot();

  const getServerSnapshot = () => serverSnapshot;

  const subscribe = useCallback((callback: () => void) => {
    if (typeof window === "undefined") return () => {};

    // Subscribe to battery events if available
    getBatteryManager().then((battery) => {
      if (battery) {
        battery.addEventListener("chargingchange", callback);
        battery.addEventListener("levelchange", callback);
      }
    });

    // Clean up
    return () => {
      if (batteryManager) {
        batteryManager.removeEventListener("chargingchange", callback);
        batteryManager.removeEventListener("levelchange", callback);
      }
    };
  }, []);

  return useSyncExternalStore(subscribe, getSnapshot, getServerSnapshot);
}

export default useLowPowerDevice;

import type { Transition, Variants } from "motion/react";

// =============================================================================
// SPRING PRESETS
// Spring physics configurations for animations
// =============================================================================

/**
 * Spring presets for consistent animations
 */
export const springPresets = {
  /**
   * Snappy - Quick, responsive interactions (buttons, toggles)
   * High stiffness, high damping = fast and controlled
   */
  snappy: {
    type: "spring" as const,
    stiffness: 400,
    damping: 30,
    mass: 1,
  },

  /**
   * Bouncy - Playful, attention-grabbing (notifications, toasts)
   * Medium stiffness, lower damping = visible bounce
   */
  bouncy: {
    type: "spring" as const,
    stiffness: 300,
    damping: 20,
    mass: 1,
  },

  /**
   * Smooth - Slow transitions (modals, sheets)
   * Lower stiffness, higher damping = graceful movement
   */
  smooth: {
    type: "spring" as const,
    stiffness: 200,
    damping: 25,
    mass: 1,
  },

  /**
   * Gentle - Subtle, background animations (fade, scale)
   * Very low stiffness, high damping = barely perceptible
   */
  gentle: {
    type: "spring" as const,
    stiffness: 150,
    damping: 30,
    mass: 1,
  },

  /**
   * Interactive - Drag and gesture responses
   * Balanced for real-time responsiveness
   */
  interactive: {
    type: "spring" as const,
    stiffness: 500,
    damping: 35,
    mass: 0.8,
  },
} as const;

// =============================================================================
// TRANSITION PRESETS
// Pre-configured transitions for common use cases
// =============================================================================

export const transitions = {
  /** Standard transition using snappy spring */
  default: springPresets.snappy as Transition,

  /** For modal/dialog opening animations */
  modal: springPresets.smooth as Transition,

  /** For dropdown/popover appearance */
  popover: {
    ...springPresets.snappy,
    duration: 0.2,
  } as Transition,

  /** For morphing popover with layoutId */
  morphingPopover: {
    type: "spring" as const,
    bounce: 0.05,
    duration: 0.3,
  } as Transition,

  /** For sheet slide-in animations */
  sheet: {
    ...springPresets.smooth,
    damping: 28,
  } as Transition,

  /** For button press feedback */
  button: {
    ...springPresets.interactive,
    duration: 0.1,
  } as Transition,

  /** For icon state changes */
  icon: {
    ...springPresets.bouncy,
    duration: 0.3,
  } as Transition,

  /** For staggered list items */
  stagger: {
    staggerChildren: 0.03,
    delayChildren: 0.05,
  } as Transition,

  /** Fast fade for overlays */
  fade: {
    type: "tween" as const,
    duration: 0.15,
    ease: "easeOut",
  } as Transition,

  /** Reduced motion fallback - instant transitions */
  reduced: {
    type: "tween" as const,
    duration: 0,
  } as Transition,
} as const;

// =============================================================================
// VARIANT FACTORIES
// Create animation variants for common patterns
// =============================================================================

/**
 * Creates scale variants for press/tap interactions
 */
export function createPressVariants(scale: number = 0.97): Variants {
  return {
    idle: { scale: 1 },
    pressed: { scale },
  };
}

/**
 * Creates hover scale variants
 */
export function createHoverVariants(scale: number = 1.02): Variants {
  return {
    idle: { scale: 1 },
    hovered: { scale },
  };
}

/**
 * Creates fade + scale variants for modal-like components
 */
export function createModalVariants(options?: {
  initialScale?: number;
  exitScale?: number;
}): Variants {
  const { initialScale = 0.95, exitScale = 0.95 } = options ?? {};

  return {
    initial: {
      opacity: 0,
      scale: initialScale,
    },
    animate: {
      opacity: 1,
      scale: 1,
    },
    exit: {
      opacity: 0,
      scale: exitScale,
    },
  };
}

/**
 * Creates slide variants for sheets/drawers
 */
export function createSlideVariants(
  side: "top" | "right" | "bottom" | "left",
): Variants {
  const transforms: Record<
    string,
    { x?: string | number; y?: string | number }
  > = {
    top: { y: "-100%" },
    right: { x: "100%" },
    bottom: { y: "100%" },
    left: { x: "-100%" },
  };

  return {
    initial: { ...transforms[side], opacity: 0 },
    animate: { x: 0, y: 0, opacity: 1 },
    exit: { ...transforms[side], opacity: 0 },
  };
}

/**
 * Creates staggered children variants for lists
 */
export function createStaggerVariants(options?: {
  staggerChildren?: number;
  delayChildren?: number;
}): Variants {
  const { staggerChildren = 0.03, delayChildren = 0.05 } = options ?? {};

  return {
    initial: { opacity: 0 },
    animate: {
      opacity: 1,
      transition: {
        staggerChildren,
        delayChildren,
      },
    },
    exit: {
      opacity: 0,
      transition: {
        staggerChildren: staggerChildren / 2,
        staggerDirection: -1,
      },
    },
  };
}

/**
 * Creates item variants for staggered lists
 */
export function createStaggerItemVariants(options?: {
  y?: number;
  scale?: number;
}): Variants {
  const { y = 10, scale = 0.95 } = options ?? {};

  return {
    initial: { opacity: 0, y, scale },
    animate: { opacity: 1, y: 0, scale: 1 },
    exit: { opacity: 0, y: y / 2, scale },
  };
}

/**
 * Creates morphing popover variants with blur
 */
export function createMorphingPopoverVariants(options?: {
  initialScale?: number;
  exitScale?: number;
  initialBlur?: number;
  exitBlur?: number;
}): Variants {
  const {
    initialScale = 0.9,
    exitScale = 0.95,
    initialBlur = 10,
    exitBlur = 10,
  } = options ?? {};

  return {
    initial: {
      opacity: 0,
      scale: initialScale,
      filter: `blur(${initialBlur}px)`,
    },
    animate: {
      opacity: 1,
      scale: 1,
      filter: "blur(0px)",
      transition: {
        type: "spring",
        bounce: 0,
        duration: 0.3,
        staggerChildren: 0.05,
      },
    },
    exit: {
      opacity: 0,
      scale: exitScale,
      filter: `blur(${exitBlur}px)`,
      transition: {
        duration: 0.2,
      },
    },
  };
}

/**
 * Creates backdrop/overlay variants
 */
export function createBackdropVariants(): Variants {
  return {
    initial: { opacity: 0 },
    animate: { opacity: 1 },
    exit: { opacity: 0 },
  };
}

/**
 * Creates origin-aware scale variants for popovers/dropdowns
 * Scales from the origin point (where it appears from)
 */
export function createPopoverVariants(options?: {
  initialScale?: number;
  originX?: number;
  originY?: number;
}): Variants {
  const { initialScale = 0.95, originX = 0.5, originY = 0 } = options ?? {};

  return {
    initial: {
      opacity: 0,
      scale: initialScale,
      originX,
      originY,
    },
    animate: {
      opacity: 1,
      scale: 1,
    },
    exit: {
      opacity: 0,
      scale: initialScale,
    },
  };
}

// =============================================================================
// GESTURE PRESETS
// Common gesture configurations
// =============================================================================

export const gestures = {
  /** Standard button tap feedback */
  button: {
    whileTap: { scale: 0.97 },
    whileHover: { scale: 1.02 },
  },

  /** Subtle press for secondary actions */
  subtle: {
    whileTap: { scale: 0.99 },
  },

  /** Icon button feedback */
  iconButton: {
    whileTap: { scale: 0.9 },
    whileHover: { scale: 1.1 },
  },

  /** Card hover effect */
  card: {
    whileHover: { scale: 1.02, y: -4 },
    transition: springPresets.smooth,
  },

  /** List item hover */
  listItem: {
    whileHover: { x: 4 },
    transition: springPresets.snappy,
  },
} as const;

// =============================================================================
// DRAG CONFIGURATION
// For swipeable/draggable components
// =============================================================================

export const dragConfig = {
  /** Sheet swipe-to-dismiss threshold */
  sheet: {
    dismissThreshold: 100,
    velocityThreshold: 500,
  },

  /** Drag constraints for bounded elements */
  bounded: {
    elastic: 0.1,
    power: 0.8,
  },
} as const;

// =============================================================================
// EASING FUNCTIONS
// Cubic-bezier curves for animations
// =============================================================================

export const easings = {
  /** Standard curve */
  standard: [0.25, 0.1, 0.25, 1],
  /** Ease-in */
  easeIn: [0.42, 0, 1, 1],
  /** Ease-out */
  easeOut: [0, 0, 0.58, 1],
  /** Emphasized */
  emphasis: [0.33, 1, 0.68, 1],
  /** Linear */
  linear: [0, 0, 1, 1],
} as const;

// =============================================================================
// DURATION PRESETS
// Consistent timing values
// =============================================================================

export const durations = {
  instant: 0,
  fast: 0.1,
  normal: 0.2,
  slow: 0.3,
  slower: 0.5,
  slowest: 0.8,
} as const;

// =============================================================================
// UTILITY FUNCTIONS
// =============================================================================

/**
 * Combines multiple variant objects
 */
export function mergeVariants(...variants: Variants[]): Variants {
  return variants.reduce((acc, variant) => ({ ...acc, ...variant }), {});
}

/**
 * Creates a transition that respects reduced motion
 */
export function getTransition(
  transition: Transition,
  prefersReducedMotion: boolean,
): Transition {
  if (prefersReducedMotion) {
    return transitions.reduced;
  }
  return transition;
}

/**
 * Generates a layoutId for shared element transitions
 */
export function createLayoutId(prefix: string, id: string | number): string {
  return `${prefix}-${id}`;
}
